import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

let data = [
  {
    id: 1,
    name: "Basics",
    price: "100$",
    category: "Shirt",
    image: "./no-image.png",
  },
  {
    id: 2,
    name: "BlueBerry",
    price: "200$",
    category: "Shirt",
    image: "./no-image.png",
  },
  {
    id: 3,
    name: "BlackBerry",
    price: "100$",
    category: "Shirt",
    image: "./no-image.png",
  },
  {
    id: 1,
    name: "Tommy Hilfiguer",
    price: "500$",
    category: "Shirt",
    image: "./no-image.pn",
  },
  {
    id: 1,
    name: "RiG",
    price: "100$",
    category: "Trouser",
    image: "./no-image.png",
  },
  {
    id: 1,
    name: "US Polo",
    price: "600$",
    category: "Trouser",
    image: "./no-image.png",
  },
  {
    id: 1,
    name: "Sin",
    price: "700$",
    category: "Trouser",
    image: "./no-image.png",
  },
  {
    id: 1,
    name: "Levis",
    price: "300$",
    category: "Trouser",
    image: "./no-image.png",
  },
  {
    id: 1,
    name: "Grasim",
    price: "100$",
    category: "Tie",
    image: "./no-image.png",
  },
  {
    id: 1,
    name: "Basics",
    price: "50$",
    category: "Tie",
    image: "./no-image.png",
  },
];

function App() {
  return (
    <div
      className="container"
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        height: "100%",
      }}
    >
      <div clasName="left-arrow">
        <img
          src="./left-arrow.png"
          alt="move left"
          style={{ width: "30px", cursor: "pointer", padding: "10px" }}
        />
      </div>
      <div
        className="carousel-container"
        aria-label="product"
        style={{
          width: "600px",
          height: "200px",
          border: "1px solid",
          display: "flex",
        }}
      >
        <div
          className="item"
          aria-label="item"
          style={{
            display: "flex",
            flexDirection: "column",
            border: "1px solid grey",
          }}
        >
          <img
            src="./no-image.png"
            alt=""
            style={{ width: "200px", height: "120px" }}
          />
          <div
            class="info"
            style={{
              padding: "0 22px;",
              lineHeight: "1.2rem",
              padding: "0 22px",
            }}
          >
            <div className="name">Name</div>
            <div className="price">
              <span>Price: </span>
              <span>$20</span>
            </div>
            <div className="category">
              <span>Category: </span>
              <span>Shirts</span>
            </div>
            <div className="category"></div>
          </div>
        </div>
      </div>
      <div clasName="right-arrow">
        <img
          src="./right-arrow.png"
          alt="move right"
          style={{ width: "30px", cursor: "pointer", padding: "10px" }}
        />
      </div>
    </div>
  );
}

export default App;
