import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

let data = [];

function App() {
  return (
    <div
      className="container"
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        height: "100%",
      }}
    >
      <div clasName="left-arrow">
        <img
          src="./left-arrow.png"
          alt="move left"
          style={{ width: "30px", cursor: "pointer", padding: "10px" }}
        />
      </div>
      <div
        className="carousel-container"
        aria-label="product"
        style={{
          width: "600px",
          height: "200px",
          border: "1px solid",
          display: "flex",
        }}
      >
        <div
          className="item"
          aria-label="item"
          style={{
            display: "flex",
            flexDirection: "column",
            border: "1px solid grey",
            padding: "0 22px",
          }}
        >
          <img
            src="./no-image.png"
            alt=""
            style={{ width: "200px", height: "120px" }}
          />
          <div
            class="info"
            style={{ padding: "0 22px;", lineHeight: "1.2rem" }}
          >
            <div className="name">Name</div>
            <div className="price">
              <span>price: </span>
              <span>$20</span>
            </div>
            <div className="category">
              <span>Category: </span>
              <span>shirts</span>
            </div>
            <div className="category"></div>
          </div>
        </div>
      </div>
      <div clasName="right-arrow">
        <img
          src="./right-arrow.png"
          alt="move right"
          style={{ width: "30px", cursor: "pointer", padding: "10px" }}
        />
      </div>
    </div>
  );
}

export default App;
